INSERT INTO sc3_events.Event (PublicEventID,time_value,latitude_value,longitude_value,magnitude_value,magnitude_type,evaluationMode,evaluationStatus,latitude_uncertainty,longitude_uncertainty)  
SELECT PEvent.publicID, CONVERT_TZ(Origin.time_value,'+00:00','+02:00'), Origin.latitude_value, Origin.longitude_value, Magnitude.magnitude_value, Magnitude.type, Origin.evaluationMode, Origin.evaluationStatus, Origin.latitude_uncertainty, Origin.longitude_uncertainty 
FROM seiscomp3.Origin, seiscomp3.Magnitude, seiscomp3.Event, seiscomp3.PublicObject as POrigin, PublicObject as PMagnitude, PublicObject as PEvent 
WHERE Magnitude._oid=PMagnitude._oid AND Event.preferredOriginID=POrigin.publicID AND Event.preferredMagnitudeID=PMagnitude.publicID AND Origin._oid=POrigin._oid AND Event._oid=PEvent._oid AND Magnitude._parent_oid=Origin._oid AND Magnitude.magnitude_value >= 0.1 AND ( Event.type != "not existing" OR Event.type is NULL) AND Origin.time_value > NOW() - INTERVAL 1 MONTH  ON DUPLICATE KEY UPDATE time_value=CONVERT_TZ(Origin.time_value,'+00:00','+02:00'),latitude_value=Origin.latitude_value,longitude_value=Origin.longitude_value,magnitude_value=Magnitude.magnitude_value,magnitude_type=Magnitude.type,evaluationMode=Origin.evaluationMode,evaluationStatus=Origin.evaluationStatus,latitude_uncertainty=Origin.latitude_uncertainty,longitude_uncertainty=Origin.longitude_uncertainty;
